import React, { useEffect } from "react";
import { View, Text, SafeAreaView, StatusBar, Image, TouchableOpacity, Modal, Animated } from "react-native";
import { COLORS } from "../constants";
import { SIZES } from "../constants";

// Sqlite Import
import * as SQLite from "expo-sqlite";

function openDatabase() {
  if (Platform.OS === "web") {
    return {
      transaction: () => {
        return {
          executeSql: () => {},
        };
      },
    };
  }

  const db = SQLite.openDatabase("db.db");
  return db;
}

const db = openDatabase();

const Home = ({ navigation }) => {
  useEffect(() => {}, [
    db.transaction((tx) => {
      tx.executeSql("create table if not exists skors_timur (id INTEGER PRIMARY KEY AUTOINCREMENT, model text,value int);");
      tx.executeSql("create table if not exists skors_barat (id INTEGER PRIMARY KEY AUTOINCREMENT, model text,value int);");
      tx.executeSql("create table if not exists skors_tengah (id INTEGER PRIMARY KEY AUTOINCREMENT, model text,value int);");
      tx.executeSql("create table if not exists nama_pengguna (id INTEGER PRIMARY KEY AUTOINCREMENT, model text,value char);");
    }),
  ]);
  return (
    <View
      style={{
        paddingHorizontal: 20,
        flex: 1,
        alignContent: "center",
        justifyContent: "center",
        backgroundColor: "#252C4A",
      }}
    >
      <View
        style={{
          alignItems: "center",
          marginVertical: 12,
          paddingVertical: 12,
          alignContent: "center",
        }}
      >
        <Text
          style={{
            fontSize: 30,
            textAlign: "center",
            color: "white",
          }}
        >
          Menu Utama
        </Text>
      </View>
      <TouchableOpacity
        onPress={() => navigation.navigate("MateriMenu")}
        style={{
          backgroundColor: COLORS.secondary,
          marginVertical: 12,
          paddingVertical: 12,
          borderRadius: 16,
          alignItems: "center",
          alignContent: "center",
        }}
      >
        <Text
          style={{
            color: "white",
            fontSize: 20,
          }}
        >
          Materi
        </Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => navigation.navigate("MenuQuiz")}
        style={{
          backgroundColor: COLORS.secondary,
          marginVertical: 12,
          paddingVertical: 12,
          borderRadius: 16,
          alignItems: "center",
          alignContent: "center",
        }}
      >
        <Text
          style={{
            color: "white",
            fontSize: 20,
          }}
        >
          Quiz
        </Text>
      </TouchableOpacity>

      <TouchableOpacity
        onPress={() => navigation.navigate("Instruction")}
        style={{
          backgroundColor: COLORS.secondary,
          marginVertical: 12,
          paddingVertical: 12,
          borderRadius: 16,
          alignItems: "center",
          alignContent: "center",
        }}
      >
        <Text style={{ color: "white", fontSize: 20 }}>Petunjuk</Text>
      </TouchableOpacity>

      <Image
        source={require("../assets/images/DottedBG.png")}
        style={{
          width: SIZES.width,
          height: 130,
          zIndex: -1,
          position: "absolute",
          bottom: 0,
          left: 0,
          right: 0,
          opacity: 0.5,
        }}
        resizeMode={"contain"}
      />
    </View>
  );
};

export default Home;
