import React from "react";
import { StyleSheet, Text, SafeAreaView, ScrollView, StatusBar, View, Image } from "react-native";
import { COLORS } from "../../constants";
import data from "../../data/QuizData";
import materi from "../../data/Materi1Data";

const MateriSatu = () => {
  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView}>
        <View>
          {materi.map((materi) => {
            return (
              <Text style={styles.text}>
                <View>
                  <Text key={materi} style={styles.judul}>
                    {materi.judul}
                  </Text>
                </View>
                <View style={styles.contgambar}>
                  <Image key={materi} style={styles.gambar} resizeMode={"stretch"} source={materi.gambar} />
                </View>
                {/* {materi.materi11} */}
                {"\n"}
                {materi.isi}
              </Text>

              // <View>
              //   <Text>{data.question}</Text>
              // </View>
            );
          })}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
    backgroundColor: COLORS.primary,
  },
  scrollView: {
    backgroundColor: "transparent",
    marginHorizontal: 10,
    paddingVertical: 10,
    paddingHorizontal: 12,
    marginBottom: 20,
    borderRadius: 12,
  },
  text: {
    textAlign: "justify",
    fontSize: 18,
    color: "white",
    paddingTop: 0,
  },
  judul: {
    fontSize: 20,
    color: "white",
  },
  text2: {
    textAlign: "justify",
    fontSize: 18,
    color: "white",
    paddingTop: 15,
  },
  contgambar: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    alignContent: "center",
    paddingVertical: 20,
  },
  gambar: {
    width: 300,
    height: 200,
  },
});

export default MateriSatu;
