import React, { useState, useEffect } from "react";
import { View, Text, TouchableOpacity, Image, StyleSheet } from "react-native";
import { SIZES } from "../constants";
import { COLORS } from "../constants";

const Instruction = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Petunjuk</Text>
      <Text style={styles.textIsi}>
        Untuk memulai, silahkan menuju halaman utama/home terlebih dahulu, kemudian pilih menu materi untuk menyimak semua materi yang ada, kemudian setelah menyimak materi, silahkan kembali ke menu utama kemudian pilih menu quiz untuk
        menguji tingkat pemahaman anda. GoodLuck
      </Text>
      <Image
        source={require("../assets/images/DottedBG.png")}
        style={{
          width: SIZES.width,
          height: 130,
          zIndex: -1,
          position: "absolute",
          bottom: 0,
          left: 0,
          right: 0,
          opacity: 0.5,
        }}
        resizeMode={"contain"}
      />
    </View>
  );
};

export default Instruction;

const styles = StyleSheet.create({
  container: {
    backgroundColor: COLORS.primary,
    flex: 1,
    alignContent: "center",
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    marginHorizontal: 20,
    marginVertical: 12,
    color: "white",
    textAlign: "center",
    fontSize: 30,
    fontWeight: "bold",
  },
  textIsi: {
    marginHorizontal: 20,
    marginVertical: 12,
    color: "white",
    textAlign: "justify",
    fontSize: 15,
  },
});
