import React, { useState, useEffect } from "react";
import { View, Text, TouchableOpacity, Image, StyleSheet, TextInput, Modal } from "react-native";
import { Colors } from "react-native/Libraries/NewAppScreen";
import { SIZES } from "../constants";
import { COLORS } from "../constants";
import * as SQLite from "expo-sqlite";

const InputNama = ({ navigation }) => {
  const [nama, setNama] = useState("");
  const [showScoreModal, setShowScoreModal] = useState(false);

  //database
  function openDatabase() {
    if (Platform.OS === "web") {
      return {
        transaction: () => {
          return {
            executeSql: () => {},
          };
        },
      };
    }

    const db = SQLite.openDatabase("db.db");
    return db;
  }

  const db = openDatabase();

  //next Button
  const userSave = (navigate) => {
    if (nama == "") {
      // Last Question
      // Show Score Modal
      console.log("masukkan nama terlebih dahulu");
    } else {
      db.transaction((tx) => {
        tx.executeSql(
          `INSERT INTO nama_pengguna (model,value) VALUES (?,?)`,
          ["Nama Pengguna", nama],
          (txObj, resultSet) => console.log("Success", nama),
          (txObj, error) => console.log("Error", error)
        );
      });
      setShowScoreModal(true);
    }
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={userSave}
        style={{
          marginTop: -20,
          width: "80%",
          backgroundColor: COLORS.accent,
          padding: 20,
          borderRadius: 5,
          marginTop: 10,
        }}
      >
        <Text style={{ fontSize: 20, color: COLORS.white, textAlign: "center" }}>Test Modal</Text>
      </TouchableOpacity>

      <Modal animationType="slide" transparent={true} visible={showScoreModal}>
        <View
          style={{
            flex: 1,
            backgroundColor: COLORS.primary,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <View
            style={{
              backgroundColor: COLORS.white,
              width: "90%",
              borderRadius: 20,
              padding: 20,
              alignItems: "center",
            }}
          >
            <Text style={{ fontSize: 28, fontWeight: "bold", textAlign: "center" }}>Masukkan nama anda untuk memulai</Text>
            <TextInput
              style={{
                borderColor: COLORS.secondary + "40",
                backgroundColor: COLORS.secondary + "20",
                borderWidth: 3,
                height: 70,
                borderRadius: 20,
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-between",
                paddingHorizontal: 20,
                marginVertical: 10,
                color: COLORS.primary,
                textDecorationStyle: "none",
                fontSize: 20,
                width: "90%",
              }}
              placeholder="Masukkan nama anda"
              placeholderTextColor={COLORS.accent + 70}
              onChangeText={(__text) => setNama(__text)}
              value={nama}
            />
            {/* Retry Quiz button */}
            <TouchableOpacity
              onPress={() => navigation.navigate("Home")}
              style={{
                backgroundColor: COLORS.accent,
                padding: 20,
                width: "90%",
                borderRadius: 20,
                margin: 8,
              }}
            >
              <Text
                style={{
                  textAlign: "center",
                  color: COLORS.white,
                  fontSize: 20,
                }}
              >
                Next
              </Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </View>
  );
};

export default InputNama;

const styles = StyleSheet.create({
  container: {
    backgroundColor: COLORS.primary,
    flex: 1,
    alignContent: "center",
    alignItems: "center",
    justifyContent: "center",
    paddingHorizontal: 20,
  },
  title: {
    marginHorizontal: 20,
    marginVertical: 12,
    color: "white",
    textAlign: "center",
    fontSize: 30,
    fontWeight: "bold",
  },
  textIsi: {
    marginHorizontal: 20,
    marginVertical: 12,
    color: "white",
    textAlign: "center",
    fontSize: 15,
  },
});
